import unittest

from solution01 import Solution

testCase = 5
solution = Solution()


class Test(unittest.TestCase):
    def test(self):
        subjects = [
            ("aaabb", 3, 3),
            ("ababbc", 2, 5),
            ("cababb", 2, 5),
            ("cababbc", 2, 7),
            ("", 1, 0),
            (
                "",
                301,
                301,
            ),
        ]

        if testCase == -1:
            for e in subjects:
                self.assertEqual(solution.longestSubstring(e[0], e[1]), e[2])
                print("==================================")
        else:
            self.assertEqual(
                solution.longestSubstring(subjects[testCase][0], subjects[testCase][1]),
                subjects[testCase][2],
            )


if __name__ == "__main__":
    unittest.main()
